mapper.select:
SELECT * FROM stock


        AND name LIKE CONCAT('%', #{name}, '%')


        AND code = #{code}


        AND type = #{type}


        AND area_id = #{areaId}


        AND parent_id = #{parentId}


        AND hc_id = #{hcId}


ORDER BY create_date DESC

    LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM stock


        AND name LIKE CONCAT('%', #{name}, '%')


        AND code = #{code}


        AND type = #{type}


        AND area_id = #{areaId}


        AND parent_id = #{parentId}


        AND hc_id = #{hcId}
