mapper.select:
SELECT * FROM supplier


        AND name LIKE CONCAT('%', #{name}, '%')


        AND status = #{status}


        AND data_type = #{dataType}


        AND business_category = #{businessCategory}


ORDER BY create_date DESC

    LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM supplier


        AND name LIKE CONCAT('%', #{name}, '%')


        AND status = #{status}


        AND data_type = #{dataType}


        AND business_category = #{businessCategory}
